mapper.select:
SELECT id, username userName, user_password userPassword, user_email userEmail, user_info userInfo, head_img headImg, create_time createTime FROM sys_user
AND username like concat('%', #{userNameLike}, '%')
AND user_email = #{userEmail}

SELECT id, username userName, user_password userPassword, user_email userEmail, user_info userInfo, head_img headImg, create_time createTime from sys_user where 1 = 1
and id = #{id}
and username = #{userName};
and 1 = 2
